import { AdRequest, AdResponse } from "../../../apis/adServer/AdServer";
export type BannerAdDimension = "600x160" | "300x250";
export interface BannerAdConfig {
    dimensions?: BannerAdDimension;
}
export interface BannerAdRequest extends AdRequest {
}
export interface BannerAdSuccessResponse extends AdResponse {
    success: true;
    data: {
        id: string;
        ctaUrl?: string;
        dimensions: {
            width: string;
            height: string;
        };
        mediaUrl: string;
    };
}
